    return async (dispatch, getState) =>{
        const authData = {
            email,password, returnSecureToken: true
        };
        const dbName = authData.email.replace('@', '').replace('.', '');
        if (!isLogin) {
            console.log(authData);
            const dataForBD = {
                email,
                listOfTasks:{

