import React from 'react'
import classes from './Tasker.css'
import MyForm from "../../components/FormOftasks/MyForm";
import ListOfTasks from '../../components/ListOfTasks/ListOfTasks'

class Tasker extends React.Component{
    state={
        name: ''
    };
    ChangeButtonHandler= (e)=>{
        this.setState({
            name: e.target.value
        })


    };


    render(){
        return(
            <div className={classes.Tasker}>
                <h1>{this.state.name === '' ? 'Input Your name' : this.state.name}</h1>
                <div>
                <label htmlFor="">Input Your name: </label>
                <input type="text" onChange={this.ChangeButtonHandler}/>
                </div>
                <MyForm
                />

            </div>
        )

    }


};

export default Tasker
