import React from 'react'
import classes from './Tasker.css'
import MyForm from "../../components/FormOftasks/MyForm";
import ListOfTasks from '../../components/ListOfTasks/ListOfTasks'

class Tasker extends React.Component{
    state={
        name: '',
        tasks:[{nameOfTask}]
    };

    onChangeNameHandler = (e)=>{
      this.setState({
          name: e.target.value
      })
    };

    render(){
        return(
            <div className={classes.Tasker}>

                <MyForm
                changeNameFunc={this.onChangeNameHandler}
                name={this.state.name}

                />
             <ListOfTasks name={this.state.name}/>
            </div>
        )

    }


};

export default Tasker
