export function addTask(taskName, description, endDate) {
    return (dispatch, getState)=>{
        // let taskList = getState().tasks;
        // if (taskList == null)
        let taskList = [];
          if (getState().tasks){
              taskList = getState().tasks

          };
          const newTask = {
              taskName, description, endDate
          };
          taskList.push(newTask);


        dispatch(forReducer(taskList))
    }

}
function forReducer(list) {
    return{
        type:'ADD_TASK',
        list

    }


}
