import axios from 'axios'

export function auth(email, password, isLogin) {
    return async dispatch =>{
        const authData = {
            email,
            password,
            returnSecureToken: true
        };

        let url ='https://www.googleapis.com/identitytoolkit/v3/relyingparty/signupNewUser?key=AIzaSyDJHO8YGwRavnNLmCryDKXOic-ChroRlFo'

        if (isLogin){
            url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/verifyPassword?key=AIzaSyDJHO8YGwRavnNLmCryDKXOic-ChroRlFo'


        }
            const response = await axios.post(url, authData);
        console.log(response.data)


    }
}
