export function addTask(taskName, description, endDate) {
    return (dispatch, getState)=>{
        let taskList = getState().tasks;
        if (taskList == null)
            taskList = [];


        const newTask={
            taskName,
            description,
            endDate
        };
        console.log(taskList)

        taskList.push(newTask);
        console.log(taskList)


