import React, {Fragment} from 'react'
import classes from './Landing.css'
import Button from '../../components/UI/Button/Button'
import Input from "../../components/UI/Input/Input";
import is from 'is_js'
import {connect} from "react-redux";
import {auth} from "../../store/actions/auth";
import {NavLink} from 'react-router-dom'
import image from '../../images/checked.svg'
import LogIn from '../LogIn/LogIn'



class Landing extends React.Component{
    state = {
        allValid: false,
        formControls:{
            Email:{
                value: '',
                isValid: true,
                isTouched: false,
                type: 'text',
                label:'Email'
            },
            password:{
                value: '',
                isValid:true,
                isTouched:false,
                type: 'password',
                label:'password'
            }
        }

    };
    onChangeHandler = (e, control)=>{
        control.isTouched = true;
        control.value = e.target.value;
        const formControls = this.state.formControls;
        if (control.label === 'Email'){
            if (is.email(control.value)){
                control.isValid = true
            }
            else {
                control.isValid = false
            }

        }
        if (control.label === 'password'){
            if(control.value.length >=6){
                control.isValid = true
            }
            else {
                control.isValid = false
            }
        }
        formControls[control.label] = control;


        this.setState({
            formControls
        });
        const names = Object.keys(this.state.formControls);



        let count = 0;
        for (let i=0; i<names.length;i++){
            if (formControls[names[i]].isValid && formControls[names[i]].isTouched){
                count ++;

            }
        }
        if (count === names.length){
            this.setState({
                allValid: true

            })

        }

    };
     enterButtonClickHandler = (x) =>{
         x ?
             this.props.auth(this.state.formControls.Email.value, this.state.formControls.password.value, true) :
             this.props.auth(this.state.formControls.Email.value, this.state.formControls.password.value, false)



         this.setState({
             allValid: false,
             formControls:{
                 Email:{
                     value: '',
                     isValid: true,
                     isTouched: false,
                     type: 'text',
                     label:'Email'
                 },
                 password:{
                     value: '',
                     isValid:true,
                     isTouched:false,
                     type: 'password',
                     label:'password'
                 }
             }

         })

     }
     onEnterButtonClickHandler=()=>{
         this.enterButtonClickHandler(1);



        }

    render(){

        const email = this.state.formControls.Email;
        const password = this.state.formControls.password;
    return(
        <Fragment>
        <div className={classes.Landing}>
            <div>

                {/*<div>*/}
                    {/*<Input isValid={email.isValid} label={email.label} value={email.value} onChange={(e)=>this.onChangeHandler(e, email)}/>*/}
                    {/*<Input label={password.label} isValid={password.isValid} type={password.type} value={password.value} onChange={(e)=>this.onChangeHandler(e, password)}/>*/}
                    {/*<Button disabled={!this.state.allValid} onClick={()=>this.enterButtonClickHandler(0)}>Зарегестрироваться</Button>*/}
                    {/*<NavLink to={'/main'} >*/}
                        {/*<Button type={'green'} disabled={!this.state.allValid} onClick={()=>this.onEnterButtonClickHandler()}>Войти</Button>*/}
                    {/*</NavLink>*/}
                {/*</div>*/}
                <h1>Держите свою жизнь под контролем</h1>
                <h2>c BUMAGAGA выполнять повседневные задачи стало проще</h2>
                <ul>
                    <li>
                        <object data={image} type="image/svg+xml"></object>
                        <span>Планируете ли вы отпуск?</span>
                    </li>
                    <li>
                        <object data={image} type="image/svg+xml"></object>
                        <span>А может управляете одновременно несколькими проектами?</span>
                    </li>
                    <li>
                        <object data={image} type="image/svg+xml"></object>
                        <span>Хотите ли составить список фильмов для просмотра?</span>
                    </li>
                    <p>Вам поможет BUMAGAGA, здесь вы можете планировать выполнение, как личных, так и профессиональных задач.</p>



                </ul>



            </div>


        </div>




        </Fragment>
    )

    }

}

function mapDispatchToProps(dispatch) {
    return{
        auth:(email, password, isLogin)=> dispatch(auth(email,password, isLogin)),

    }

}
function mapStateToProps(state) {
    return{
        state: state.auth
    }

}

export default connect(mapStateToProps, mapDispatchToProps)(Landing);
