            console.log(authData);

            const response = await axios.post('https://www.googleapis.com/identitytoolkit/v3/relyingparty/signupNewUser?key=AIzaSyDJHO8YGwRavnNLmCryDKXOic-ChroRlFo', authData);
            const dbName = authData.email.replace('@', '').replace('.', '');
            axios.put(`https://taskscheduler-be7db.firebaseio.com/users/<${dbName}>.json`, authData );



        }
