import React, {Fragment} from 'react'
import classes from './Tasker.css'
import MyForm from "../../components/FormOftasks/MyForm";
import ListOfTasks from '../../components/ListOfTasks/ListOfTasks'
import Header from "../../components/Header/Header";
import {connect} from 'react-redux'

class Tasker extends React.Component{
    state={
        tasks:[
            {nameOfTask: 'Сделать Дз', description: 'kek', endDate:'22 january'},
            {nameOfTask: 'Приготовить ужин', description: 'kek', endDate:'22 january'},
            {nameOfTask: 'Заказать линзы', description: 'kek', endDate:'22 january'}
            ]
    };

    onChangeNameHandler = (e)=>{
      this.setState({
          name: e.target.value
      })
    };

    addTaskHandler= ()=>{
        const nameTask = document.querySelector('#nameTask').value;
        const description = document.querySelector('#description').value;
        const endDate = document.querySelector('#endDate').value;
        if (nameTask && description && endDate) {
            let tasks = this.state.tasks;
            tasks.push({nameOfTask: nameTask, description: description, endDate: endDate});
            this.setState({
                tasks
            })
        }

    };
    deleteTaskButtonHandler = (index)=>{
        const tasks= this.state.tasks;
        index ===0 ? tasks.shift() : tasks.splice(index,index);
        this.setState({
            tasks
        })



    };

    render(){
        console.log(this.props.state)
        return(
            <Fragment>
                <Header/>

                <div className={classes.Tasker}>


                <MyForm
                changeNameFunc={this.onChangeNameHandler}
                name={this.state.name}
                AddTask={this.addTaskHandler}

                />
             <ListOfTasks
                 name={this.state.name}
                 tasks={this.state.tasks}
                 deleteTask={this.deleteTaskButtonHandler}


             />
            </div>
            </Fragment>
        )

    }


};

function mapStateToProps (state){
    return{
        state: state.tasks
    }

}

export default connect(mapStateToProps)(Tasker)
