import axios from 'axios'

export function getTasks() {
    return async (dispatch, getState)=>{
        const name = getState().auth.keyForBD;
        const response  = await axios.get(`https://taskscheduler-be7db.firebaseio.com/users/<${name}>/listOfTasks.json`);
        const newState = response.data;
        dispatch(forReducer(newState))
        console.log('get new')

    }

}

function forReducer(newState) {
    return{
        type: 'GET_TASKS_FROM_BD',
        newState
    }

}
