import React from 'react'
import classes from './Task.css'

const Task = (props)=>{
    return(
        <div className={classes.Task}>
        {props.task.map((obj, index)=>{
            return(
                <li key={index}>
                    <h3>{obj.nameOfTask}</h3>
                    <small>{obj.endDate}</small>
                    <br/>
                    <p>{obj.description}</p>

                </li>
            )

        })}
        </div>
    )


};

export default Task
