export function deleteTaskButtonHandler(index) {

    return (dispatch,getState) =>{
        const store = getState().tasks.tasks;
        if(index === 0)
            store.shift();
        store.splice(index,index);
        dispatch(delTask(store));

    }
}
function delTask(tasks) {
    return{
        type: 'KEK',
        tasks
    }

}
