import axios from 'axios'
export function addTask(taskName, description, endDate) {
    return async (dispatch, getState)=>{
        let taskListLength = getState().tasks.length;
        if(taskListLength === undefined)
            taskListLength = 0;

          const newTask = {
              taskName, description, endDate
          };
          await axios.put(`https://taskscheduler-be7db.firebaseio.com/users/${getState().auth.keyForBD}/listOfTasks/${taskListLength}`, newTask)
        dispatch(forReducer())

    }

}
function forReducer() {
    return{
        type:'ADD_TASK',

    }


}
