import React, {Fragment} from 'react'
import classes from './Tasker.css'
import MyForm from "../../components/FormOftasks/MyForm";
import ListOfTasks from '../../components/ListOfTasks/ListOfTasks'
import Header from "../../components/Header/Header";
import {connect} from 'react-redux'
import {deleteTaskButtonHandler} from "../../store/actions/deleteTaskButtonHandler";

class Tasker extends React.Component{
    render(){
        return(
            <Fragment>
                <Header/>
                <div className={classes.Tasker}>
                <MyForm
                    onChange={this.changeInputHandler}
                />
             <ListOfTasks
                 tasks={this.props.state.tasks}
                 deleteTask={this.props.deleteTaskButtonHandler}


             />
            </div>
            </Fragment>
        )

    }


};

function mapStateToProps (state){
    return{
        state: state.tasks
    }

}
function mapDispathcToProps(dispatch) {
    return {
        deleteTaskButtonHandler: (index)=>dispatch(deleteTaskButtonHandler(index))
    }

}

export default connect(mapStateToProps, mapDispathcToProps)(Tasker)
